import React from "react";
import { createRoot } from "react-dom/client";

import "./index.css";

// List
const books = [
  {
    img: "https://m.media-amazon.com/images/I/81mpSoJzv4L._SL1446_.jpg",
    title: "I Love You to the Moon and Back",
    author: "Amelia Hepworth",
  },

  {
    title: "The Little Prince",
    author: "Antoine de Saint-Exupéry",
    img: "https://m.media-amazon.com/images/I/71OZY035QKL._SL1500_.jpg",
  },
];

const names = ["jon", "peter", "susan"];
const newName = names.map((name) => {
  return <h1>{name}'\n'</h1>;
});

function BookList() {
  return <section className="booklist">{names}</section>;
}

const Book = (props) => {
  return (
    <article className="book">
      <img src={props.img} alt="" style={{ width: 400 }} />
      <h1>{props.title}</h1>
      <h4>{props.author}</h4>
    </article>
  );
};

const root = createRoot(document.getElementById("root"));
root.render(<BookList />);
