import React from "react";
import { createRoot } from "react-dom/client";

import "./index.css";

const books = [
  {
    id: 1,
    img: "https://m.media-amazon.com/images/I/81mpSoJzv4L._SL1446_.jpg",
    title: "I Love You to the Moon and Back",
    author: "Amelia Hepworth",
  },
  {
    id: 2,
    title: "The Little Prince",
    author: "Antoine de Saint-Exupéry",
    img: "https://m.media-amazon.com/images/I/71OZY035QKL._SL1500_.jpg",
  },
  {
    id: 3,
    title: "The Rainbow Fish",
    author: "Marcus Pfister",
    img: "https://m.media-amazon.com/images/I/81YNkSjQ+XL._SL1500_.jpg",
  },
];

function BookList() {
  return (
    <section className="booklist">
      {books.map((book) => {
        return <Book key={book.id} {...book} />;
      })}
    </section>
  );
}

const Book = (props) => {
  const { img, title, author } = props;

  const clickHandler = (e) => {
    console.log(e);
    console.log(e.target);
    alert("this click is by reference");
  };

  const complexExample = (author) => {
    alert(author);
  };
  return (
    <article
      className="book"
      onMouseOver={() => {
        console.log(title);
      }}
    >
      <img src={img} alt="" style={{ width: 400 }} />
      <h1>{title}</h1>
      <h4>{author}</h4>

      <button type="button" onClick={clickHandler}>
        Reference Example
      </button>
      <button type="button" onClick={() => alert("this click is inline")}>
        Inline Example
      </button>
      <button type="button" onClick={() => complexExample(author)}>
        more complex example
      </button>
    </article>
  );
};

const root = createRoot(document.getElementById("root"));
root.render(<BookList />);
