import React from "react";
import { createRoot } from "react-dom/client";

import "./index.css";

const firstBook = {
  img: "https://m.media-amazon.com/images/I/81mpSoJzv4L._SL1446_.jpg",
  title: "I Love You to the Moon and Back",
  author: "Amelia Hepworth",
};
const title = "The Little Prince";
const author = "Antoine de Saint-Exupéry";
const img = "https://m.media-amazon.com/images/I/71OZY035QKL._SL1500_.jpg";

function BookList() {
  // adding a <p> child into our Book component
  return (
    <section className="booklist">
      <Book
        img={firstBook.img}
        title={firstBook.title}
        author={firstBook.title}
      >
        <p>
          Lorem ipsum dolor sit amet consectetur, adipisicing elit. Animi quod
          distinctio laborum maiores voluptate! Beatae quaerat reiciendis
          architecto quod voluptas.
        </p>
      </Book>

      <Book img={img} title={title} author={author} />
    </section>
  );
}

const Book = (props) => {
  // or use the standard props syntax
  return (
    <article className="book">
      <img src={props.img} alt="" style={{ width: 400 }} />
      <h1>{props.title}</h1>
      <h4>{props.author}</h4>
      {props.children}
    </article>
  );
  // NOTE: 'children' is a keyword and must be used if you want to access children
};

const root = createRoot(document.getElementById("root"));
root.render(<BookList />);
